from PlannetClass import planet
import matplotlib.pyplot as plt
from matplotlib.animation import FuncAnimation
import math

sun = planet(1.989 * 10**30, 0, 0, 0, 0, 0, 0)

G = 6.67430e-11
M_sun = 1.989e30

def orbital_velocity(r):
    return math.sqrt(G * M_sun / r)

# Instanciate Mercury
v_mercury = orbital_velocity(57.9e9)
mercury = planet(3.285e23, 57.9e9, 0, 0, v_mercury, 0, 0)

# Instanciate Venus
v_venus = orbital_velocity(108.2e9)
venus = planet(4.867e24, 108.2e9, 0, 0, v_venus, 0, 0)

# Instanciate Earth
v_earth = orbital_velocity(149.6e9)
earth = planet(5.972e24, 149.6e9, 0, 0, v_earth, 0, 0)

# Instanciate Mars
v_mars = orbital_velocity(227.9e9)
mars = planet(6.39e23, 227.9e9, 0, 0, v_mars, 0, 0)

# Instanciate Jupiter
v_jupiter = orbital_velocity(778.5e9)
jupiter = planet(1.898e27, 778.5e9, 0, 0, v_jupiter, 0, 0)

# Instanciate Saturn
v_saturn = orbital_velocity(1.433e12)
saturn = planet(5.683e26, 433e12, 0, 0, v_saturn, 0, 0)

# Instanciate Uranus
v_uranus = orbital_velocity(2.872e12)
